import React, { useState } from 'react';
import { PlusIcon } from '@heroicons/react/24/outline';
import './BugForm.css';

const BugForm = ({ onBugCreated }) => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    severity: 'Low'
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    // Clear error when user starts typing
    if (error) setError('');
  };

  const handleSeverityChange = (severity) => {
    setFormData({
      ...formData,
      severity: severity
    });
    if (error) setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Basic validation
    if (!formData.title.trim() || !formData.description.trim()) {
      setError('Please fill in all required fields');
      return;
    }

    setIsSubmitting(true);
    setError('');

    try {
      await onBugCreated(formData);
      // Reset form after successful submission
      setFormData({
        title: '',
        description: '',
        severity: 'Low'
      });
    } catch (err) {
      setError('Failed to create bug. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const severityOptions = [
    { name: 'Low', color: 'green' },
    { name: 'Medium', color: 'yellow' },
    { name: 'High', color: 'orange' },
    { name: 'Critical', color: 'red' }
  ];

  return (
    <div className="bug-form-container">
      <div className="bug-form-header">
        <div className="bug-form-header-content">
          <PlusIcon className="bug-form-icon" />
          <div>
            <h2 className="bug-form-title">New Bug Report</h2>
            <p className="bug-form-subtitle">Report a new issue or bug</p>
          </div>
        </div>
      </div>

      {error && (
        <div className="bug-form-error">
          <p className="bug-form-error-text">{error}</p>
        </div>
      )}

      <form onSubmit={handleSubmit} className="bug-form">
        <div className="form-field">
          <label htmlFor="title" className="form-label">
            Title *
          </label>
          <input
            type="text"
            id="title"
            name="title"
            value={formData.title}
            onChange={handleChange}
            placeholder="Brief description of the issue"
            className="form-input"
            disabled={isSubmitting}
            maxLength={100}
          />
          <div className="character-count">
            {formData.title.length}/100
          </div>
        </div>

        <div className="form-field">
          <label htmlFor="description" className="form-label">
            Description *
          </label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
            placeholder="Detailed steps to reproduce, expected behavior, actual behavior..."
            className="form-textarea"
            disabled={isSubmitting}
            maxLength={500}
          />
          <div className="character-count">
            {formData.description.length}/500
          </div>
        </div>

        <div className="form-field">
          <label className="form-label">
            Severity Level
          </label>
          <div className="severity-buttons">
            {severityOptions.map(option => (
              <button
                key={option.name}
                type="button"
                onClick={() => handleSeverityChange(option.name)}
                className={`severity-button severity-${option.name.toLowerCase()} ${
                  formData.severity === option.name ? 'active' : ''
                }`}
                disabled={isSubmitting}
              >
                {option.name}
              </button>
            ))}
          </div>
        </div>

        <button
          type="submit"
          disabled={isSubmitting || !formData.title.trim() || !formData.description.trim()}
          className="submit-button"
        >
          {isSubmitting ? (
            <>
              <div className="loading-spinner"></div>
              Creating Bug...
            </>
          ) : (
            <>
              <PlusIcon style={{ width: '18px', height: '18px' }} />
              Create Bug Report
            </>
          )}
        </button>
      </form>
    </div>
  );
};

export default BugForm;
